import React, { useState } from "react";

function CreateArea() {
	const [newTitle, setNewTitle] = useState({
		title: "",
		content: "",
	});

	return (
		<div>
			<form>
				<input name="title" placeholder="Title" />
				<textarea name="content" placeholder="Take a note..." rows="3" />
				<button>Add</button>
			</form>
		</div>
	);
}

export default CreateArea;
