import React, { useState } from "react";

function CreateArea() {
	const [newNote, setNewNote] = useState({
		title: "",
		content: "",
	});
	const addItem = () => {
		setNewNote(newNote);
	};

	return (
		<div>
			<form>
				<input name="title" value={newNote} placeholder="Title" />
				<textarea name="content" placeholder="Take a note..." rows="3" />
				<button>Add</button>
			</form>
		</div>
	);
}

export default CreateArea;
